# Pythonimport pandas as pdfrom prophet import Prophetm = Prophet()# df = pd.read_csv('https://raw.githubusercontent.com/facebook/prophet/main/examples/example_wp_log_peyton_manning.csv')# print(df.head())# Dow_Jones_Commodity_Index   = pd.read_csv('Dow_Jones_Industrial_Average.csv')# Dow_Jones_Commodity_Index_close = Dow_Jones_Commodity_Index[['datetime','close']].copy()# Dow_Jones_Commodity_Index_close.rename(columns = {'datetime':'ds', 'close':'y'}, inplace = True)# m.fit(Dow_Jones_Commodity_Index_close)# Dow_Jones_Industrial_Average    = pd.read_csv('Dow_Jones_Commodity_Index.csv')# Dow_Jones_Industrial_Average_close = Dow_Jones_Industrial_Average[['datetime','close']].copy()# Dow_Jones_Industrial_Average_close.rename(columns = {'datetime':'ds', 'close':'y'}, inplace = True)# m.fit(Dow_Jones_Industrial_Average_close)pd_btc_usd = pd.read_csv('pd_btc_usd.csv')pd_btc_usd_close = pd_btc_usd[['datetime','close']].copy()pd_btc_usd_close.rename(columns = {'datetime':'ds', 'close':'y'}, inplace = True)m.fit(pd_btc_usd_close)future = m.make_future_dataframe(periods=365)future.tail()forecast = m.predict(future)forecast[['ds', 'yhat', 'yhat_lower', 'yhat_upper']].tail()fig1 = m.plot(forecast)fig2 = m.plot_components(forecast)